package sivl.platform.common.annotation;import java.lang.annotation.ElementType;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;/** * 重复提交注解<br/> * 目的：防止重复请求 * @author lixuefeng * @createTime 2015年11月28日 * @version 1.0 */@Target(ElementType.METHOD)@Retention (RetentionPolicy.RUNTIME)public @interface Resubmit {		/**	 * 需要保存令牌<br/>	 * 默认：true	 * @return boolean	 */	boolean needSaveToken() default true ;	/**	 * 移除保存令牌<br/>	 * 默认：true	 * @return boolean	 */    boolean needRemoveToken() default true ;}