package sivl.platform.common.encrypt;import java.io.IOException;import java.security.GeneralSecurityException;import java.security.MessageDigest;import sivl.platform.common.constant.Constant;/** * 进制转换<br/> * 目的：提供各种进制转换，为各加密算法提供支持 * @author lixuefeng * @createTime 2015年11月28日 * @version 1.0 */public final class HexConversion {		/**     * 二进制转十六进制字符串     * @param bytes     * @return     */    public static String byte2hex(byte[] bytes) {        StringBuilder sign = new StringBuilder();        for (int i = 0; i < bytes.length; i++) {            String hex = Integer.toHexString(bytes[i] & 0xFF);            if (hex.length() == 1) {                sign.append("0");            }            sign.append(hex.toUpperCase());        }        return sign.toString();    }        /**     * 进行sha1计算     * @param data     * @return     * @throws IOException     */    public static byte[] getSHA1Digest(String data) throws IOException {        byte[] bytes = null;        try {            MessageDigest md = MessageDigest.getInstance("SHA-1");            bytes = md.digest(data.getBytes(Constant.UTF8.getValue()));        } catch (GeneralSecurityException gse) {            throw new IOException(gse);        }        return bytes;    }}