package sivl.platform.common.service;import java.util.List;import sivl.platform.common.model.SysParamModel;/** * 系统参数<br/> * 目的：用于读取系统配置信息 * @author lixuefeng * @createTime 2015年12月10日 * @version 1.0 */public interface SysParamService {		/**	 * 取得所有系统参数集合	 * @return	 */	List<SysParamModel> getAllSysParamList();		/**	 * 取得所有开启的系统参数集合	 * @return	 */	List<SysParamModel> getAllEnabledSysParamList();		/**	 * 根据分组名取得系统参数集合	 * @param groupName	 * @return	 */	List<SysParamModel> getSysPatamListByGroupName(String groupName);		/**	 * 根据分组名取得所有开启的系统参数集合	 * @param groupName	 * @return	 */	List<SysParamModel> getSysPatamListEnabledByGroupName(String groupName);		/**	 * 根据参数编码取得系统参数<br/>	 * ruturn null 参数不存在	 * @param code	 * @return	 */	SysParamModel getSysParamByCode(String code);		/**	 * 根据参数编码取得开启的系统参数<br/>	 * ruturn null 参数不存在	 * @param code	 * @return	 */	SysParamModel getSysParamEnableByCode(String code);		/**	 * 根据编码取得参数值<br/>	 * ruturn null 参数不存在	 * @author lixuefeng	 * @createTime 2015年12月22日	 * @param code	 * @return	 */	String getValue(String code);}