package sivl.platform.common.utils;import java.util.UUID;import javax.servlet.http.HttpServletRequest;import sivl.platform.common.constant.Constant;/** * 简单工具集合<br/> * @author lixuefeng * @createTime 2015年11月30日 * @version 1.0 */public class SimpleUtil {	/**	 * 取得用户seesion参数名密钥	 * @param request	 * @return	 */	public static String getUserSessionKey(HttpServletRequest request){		String sign = null;		try {			sign = request.getParameter(Constant.SIGN_NAME.getValue());		} catch (Exception e) {			sign = "";		}		return Constant.USER_SESSION_KEY.getValue()+sign;	}		/**	 * 取得UUID	 * @author lixuefeng	 * @createTime 2015年12月22日	 * @return	 */	public static String getUUID(){		UUID uuid = UUID.randomUUID();		return uuid.toString();	}		/**	 * 取得32位UUID	 * @author lixuefeng	 * @createTime 2015年12月22日	 * @return	 */	public static String getUUID32(){		UUID uuid = UUID.randomUUID();		String result = uuid.toString();		result = result.replaceAll("-", "");		return result;	}}