package sivl.platform.common.utils;import org.springframework.util.StringUtils;/** * 字符串工具类<br/> * 目的：进行字符串处理 * @author lixuefeng * @createTime 2015年11月21日 * @version 1.0 */public class StringUtil extends StringUtils {		/**	 * 功能说明：判断字符串是否为非空	 * @param string	 * @return boolean	 */	public static boolean isNotEmpty(String string){		return !isEmpty(string);	}		/**	 * 功能说明：判断字符串是不是为空	 * @param string	 * @return boolean	 */	public static boolean isEmpty(String string){		return string==null || string.trim().length()==0;	}		/**	 * 功能说明：String转成int类型	 * @param string	 * @return int	 */	public static int StringToInt(String string){		int value = 0;		if(isEmpty(string)){			return value;		}		try{			value = Integer.parseInt(string);		}catch(Exception e){}		return value;	}		/**	 * 功能说明：String转成double类型	 * @param string	 * @return double	 */	public static double StringToDouble(String string){		double value = 0;		if(isEmpty(string)){			return value;		}		try{			value = Double.parseDouble(string);		}catch(Exception e){}		return value;	}		/**	 * 功能说明：String转成float类型	 * @param string	 * @return float	 */	public static float StringToFloat(String string){		float value = 0;		if(isEmpty(string)){			return value;		}		try{			value = Float.parseFloat(string);		}catch(Exception e){}		return value;	}		/**	 * 功能说明：String转成long类型	 * @param string	 * @return long	 */	public static long StringToLong(String string){		long value = 0;		if(isEmpty(string)){			return value;		}		try{			value = Long.parseLong(string);		}catch(Exception e){}		return value;	}		/**	 * 功能说明：String转成boolean类型	 * @param string	 * @return boolean	 */	public static boolean StringToBoolean(String string){		boolean value = false;		if(isEmpty(string)){			return value;		}		try{			value = Boolean.parseBoolean(string.toLowerCase());		}catch(Exception e){}		return value;	}		/**	 * 功能说明：向右补全	 * @param str	 * @param length	 * @param theChar	 * @return String	 */	public static String rightPad(String str,int length,char theChar){		String result = str;		if(result!=null&& result.length()<length){			for(int a=0;a<length-result.length();a++){				result+=theChar;			}		}		return result;	}		}