package sivl.platform.common.utils;import com.thoughtworks.xstream.XStream;import com.thoughtworks.xstream.io.xml.DomDriver;/** * xml工具类 目的： *  * @author lixuefeng * @createTime 2015年12月22日 * @version 1.0 */public class XmlUtil {	/**	 * 对象转xml	 * 	 * @author lixuefeng	 * @createTime 2015年12月22日	 * @param obj	 * @return	 */	public static String bean2xml(Object obj) {		XStream xStream = new XStream(new DomDriver());		xStream.alias(obj.getClass().getSimpleName(), obj.getClass());		String xml = xStream.toXML(obj);		return xml;	}	/**	 * xml转对象	 * @author lixuefeng	 * @createTime 2015年12月22日	 * @param xml	 * @param obj	 * @return	 */	public static Object xml2object(String xml, Object obj) {		XStream xStream = new XStream(new DomDriver());		xStream.alias(obj.getClass().getSimpleName(), obj.getClass());		Object reobj = xStream.fromXML(xml);		return reobj;	}}