package sivl.platform.file.client.custom;import java.io.File;import java.io.IOException;import java.util.Map;import sivl.platform.file.model.FileResultModel;/** * 文件存储操作客户端 * 目的： * @author lixuefeng * @date 2016年1月6日 * @version 1.0 */public interface StorageClient {		/**	 * 上传文件	 * 目的：	 * @author lixuefeng	 * @date 2016年1月6日	 * @param file 需要上传的文件	 * @param fileType 文件类型	 * @param storePathIndex 文件路径byte	 * @return	 * @throws IOException	 */	public FileResultModel<String> upload(File file,String fileType,byte storePathIndex) throws IOException;		/**	 * 上传文件	 * 目的：	 * @author lixuefeng	 * @date 2016年1月7日	 * @param byteArray 需要上传的文件byte数组	 * @param fileType 文件类型	 * @param storePathIndex 文件路径byte	 * @return	 * @throws IOException	 */	public FileResultModel<String> upload(byte[] byteArray,String fileType,byte storePathIndex) throws IOException;		/**	 * 上传文件	 * 目的：	 * @author lixuefeng	 * @date 2016年1月7日	 * @param group 组名	 * @param fileName 文件名	 * @return	 * @throws IOException	 */	public FileResultModel<Boolean> delete(String group,String fileName) throws IOException;		/**	 * 设置文件附加信息	 * 目的：	 * @author lixuefeng	 * @date 2016年1月7日	 * @param group 组名	 * @param fileName 文件名	 * @param meta 附件信息	 * @return	 * @throws IOException	 */	public FileResultModel<Boolean> setMeta(String group,String fileName,Map<String,String> meta) throws IOException;		/**	 * 取得文件附加信息	 * 目的：	 * @author lixuefeng	 * @date 2016年1月7日	 * @param group 组名	 * @param fileName 文件名	 * @return	 * @throws IOException	 */	public FileResultModel<Map<String,String>> getMeta(String group,String fileName) throws IOException;		/**	 * 关闭客户端	 * 目的：	 * @author lixuefeng	 * @date 2016年1月7日	 * @throws IOException	 */	public void close() throws IOException;	/**	 * 指定主文件id,存为slave	 * 目的：	 * @author lixuefeng	 * @date 2016年1月7日	 * @param file 需要上传的文件	 * @param fileid 主文件id,带group,如 g1/M00/00/00/aaaabbbbccc.jpg	 * @param slavePrefix slave的后缀名 如_200x200,最终的文件名将为g1/M00/00/00/aaaabbbbccc_200x200.jpg	 * @param fileType 文件类型	 * @param meta 文件附加信息	 * @return	 * @throws IOException	 */    public FileResultModel<String> uploadSlave(File file, String fileid,String slavePrefix,String fileType,Map<String,String> meta) throws IOException;    /**     * 客户端是否关闭     * 目的：     * @author lixuefeng     * @date 2016年1月7日     * @return     */    public boolean isClosed();}