package sivl.platform.file.client.custom;import java.io.IOException;import java.util.List;import sivl.platform.file.model.FileGroupInfoModel;import sivl.platform.file.model.FileResultModel;import sivl.platform.file.model.FileStorageInfoModel;import sivl.platform.file.model.FileUploadStorageModel;/** * 跟踪服务器客服端 * 目的： * @author lixuefeng * @date 2016年1月7日 * @version 1.0 */public interface TrackerClient {		/**	 * 获取上传文件信息<br/>	 * 目的：	 * @author lixuefeng	 * @date 2016年1月7日	 * @return	 * @throws IOException	 */	public FileResultModel<FileUploadStorageModel> getUploadStorage() throws IOException;		/**	 * 获取上传文件地址<br/>	 * 目的：	 * @author lixuefeng	 * @date 2016年1月7日	 * @param group	 * @param fileName	 * @return	 * @throws IOException	 */	public FileResultModel<String> getUpdateStorageAddr(String group,String fileName) throws IOException;		/**	 * 获取下载文件地址<br/>	 * 目的：	 * @author lixuefeng	 * @date 2016年1月7日	 * @param group	 * @param fileName	 * @return	 * @throws IOException	 */	public FileResultModel<String> getDownloadStorageAddr(String group,String fileName) throws IOException;		/**	 * 获取分组信息<br/>	 * 目的：	 * @author lixuefeng	 * @date 2016年1月7日	 * @return	 * @throws IOException	 */	public FileResultModel<List<FileGroupInfoModel>> getGroupInfos() throws IOException;		/**	 * 获取文件存储信息<br/>	 * 目的：	 * @author lixuefeng	 * @date 2016年1月7日	 * @param group	 * @return	 * @throws IOException	 */	public FileResultModel<List<FileStorageInfoModel>> getStorageInfos(String group) throws IOException;		/**	 * 关闭连接<br/>	 * 目的：	 * @author lixuefeng	 * @date 2016年1月7日	 * @throws IOException	 */	public void close() throws IOException;	}