package sivl.platform.file.cmd;import java.io.IOException;import java.net.Socket;import sivl.platform.file.model.FileResultModel;/** * 关闭退出命令 * 目的： * @author lixuefeng * @date 2016年1月7日 * @version 1.0 */public class CloseCmd extends AbstractCmd<Boolean> {		public CloseCmd() {		super();		this.requestCmd = FDFS_PROTO_CMD_QUIT;	}	@Override	public FileResultModel<Boolean> exec(Socket socket) throws IOException {		request(socket.getOutputStream());		return new FileResultModel<Boolean>(SUCCESS_CODE,true);	}}