package sivl.platform.file.cmd;import java.io.IOException;import java.net.Socket;import java.util.Arrays;import sivl.platform.file.model.FileResultModel;/** * 删除命令 * 目的： * @author lixuefeng * @date 2016年1月7日 * @version 1.0 */public class DeleteCmd extends AbstractCmd<Boolean> {	public DeleteCmd(String group,String fileName) {		super();		byte[] groupByte = group.getBytes(charset);		int group_len = groupByte.length;		if (group_len > FDFS_GROUP_NAME_MAX_LEN) {			group_len = FDFS_GROUP_NAME_MAX_LEN;		}		byte[] fileNameByte = fileName.getBytes(charset);		body = new byte[FDFS_GROUP_NAME_MAX_LEN + fileNameByte.length];		Arrays.fill(body, (byte) 0);		System.arraycopy(groupByte, 0, body, 0, group_len);		System.arraycopy(fileNameByte, 0, body, FDFS_GROUP_NAME_MAX_LEN, fileNameByte.length);		this.requestCmd = STORAGE_PROTO_CMD_DELETE_FILE;		this.responseCmd = STORAGE_PROTO_CMD_RESP;		this.responseSize = 0;	}		@Override	public FileResultModel<Boolean> exec(Socket socket) throws IOException {		request(socket.getOutputStream());		Response response = response(socket.getInputStream());		if(response.isSuccess()){			return new FileResultModel<Boolean>(response.getCode(),true);		}else{			return new FileResultModel<Boolean>(response.getCode(),"Delete Error");		}	}}