package sivl.platform.file.demo;import java.io.File;import java.util.Map;import sivl.platform.file.demo.data.FastdfsInfo;public interface FastdfsClient {		public String upload(File file) throws Exception;	/**	 * 	 * 功能说明：上传文件获取上传文件对应的ID	 * @author liuli	 * @creationDate 2015年5月22日 下午2:10:53	 * @param file 上传的文件	 * @param fileType 文件类型（jpg...）	 * @return 上传文件的ID	 * @throws Exception String	 */	public String upload(File file,String fileType) throws Exception;		/**	 * 	 * 功能说明：上传文件获取上传文件对应的ID	 * @author liuli	 * @creationDate 2015年5月22日 下午2:10:56	 * @param byteArray 文件对应的byte数组	 * @param fileType 文件类型（必填）（jpg...）	 * @return 上传文件的ID	 * @throws Exception String	 */	public String upload(byte[] byteArray,String fileType) throws Exception;		/**	 * 	 * 功能说明：通过文件ID生成对应的URL	 * @author liuli	 * @creationDate 2015年5月22日 下午2:13:09	 * @param fileId 文件ID	 * @return 文件对应的url	 * @throws Exception String	 */	public String getUrl(String fileId) throws Exception;		/**	 * 	 * 功能说明：设置文件的注释信息	 * @author liuli	 * @creationDate 2015年5月22日 下午2:16:30	 * @param fileId 文件ID	 * @param meta 文件的注释信息	 * @return Boolean	 * @throws Exception 	 */	public Boolean setMeta(String fileId,Map<String,String> meta) throws Exception;		/**	 * 	 * 功能说明：根据文件的ID获取文件的注释信息	 * @author liuli	 * @creationDate 2015年5月22日 下午2:17:02	 * @param fileId 文件ID	 * @return Map<String,String>	 * @throws Exception 	 */	public Map<String,String> getMeta(String fileId) throws Exception;		/**	 * 	 * 功能说明：根据文件的ID删除对应的文件	 * @author liuli	 * @creationDate 2015年5月22日 下午2:17:53	 * @param fileId 文件ID	 * @return Boolean	 * @throws Exception 	 */	public Boolean delete(String fileId) throws Exception;		/**	 * 	 * 功能说明：关闭连接	 * @author liuli	 * @creationDate 2015年5月22日 下午2:18:22 void	 */	public void close();	/**	 * 	 * 功能说明：上传一个文件	 * @author liuli	 * @creationDate 2015年5月22日 下午2:19:13	 * @param file 要上传的文件	 * @param fileType 文件类型	 * @param meta key/value的meta data，可为null	 * @return fileid 带group的fileid	 * @throws Exception 	 */    public String upload(File file,String fileType,Map<String,String> meta) throws Exception;    /**     *      * 功能说明：上传一个文件     * @author liuli     * @creationDate 2015年5月22日 下午2:20:05     * @param byteArray 文件字节数组     * @param fileType 文件类型     * @param meta key/value的meta data，可为null     * @return fileid 带group的fileid     * @throws Exception     */    public String upload(byte[] byteArray,String fileType,Map<String,String> meta) throws Exception;    /**     *      * 功能说明：上传文件（加后缀）     * @author liuli     * @creationDate 2015年5月22日 下午2:20:34     * @param file 要上传的文件     * @param fileid 文件ID 例如：group1/M00/00/01/abc.jpg     * @param prefix 文件前缀     * @param fileType 文件类型     * @return 上传后的fileid 例如：group1/M00/00/01/abc_200x200.jpg     * @throws Exception     */    public String uploadSlave(File file,String fileid, String prefix, String fileType) throws Exception;        /**     *      * 功能说明：通过FileID获取fastdfs的信息     * @author liuli     * @creationDate 2015年5月22日 下午3:38:42     * @param fileid     */    public FastdfsInfo getFastdfsInfo(String fileid);        /**     *      * 功能说明：上传文件返回对应的Fastdfs信息     * @author liuli     * @creationDate 2015年5月22日 下午3:51:01     * @param byteArray 文件对应的byte数组     * @param fileType 文件类型     * @return FastdfsInfo 上传文件后的Fastdfs信息     */    public FastdfsInfo uploadFile(byte[] byteArray,String fileType);}