package sivl.platform.file.demo.client;import java.io.File;import java.io.IOException;import java.util.Map;import sivl.platform.file.demo.data.Result;/** *  * 主要功能：StorageClient * @author liuli * @creationDate 2015年5月22日 下午2:37:23 */public interface StorageClient {	/**	 * 	 * 功能说明：文件上传	 * @author liuli	 * @creationDate 2015年5月22日 下午2:37:52	 * @param file 需要上传的文件	 * @param fileType 文件类型	 * @param storePathIndex 文件路径byte	 * @return Result<String>	 * @throws IOException 	 */	public Result<String> upload(File file,String fileType,byte storePathIndex) throws IOException;		/**	 * 	 * 功能说明：	 * @author liuli	 * @creationDate 2015年5月22日 下午2:41:28	 * @param byteArray需要上传的文件byte数组	 * @param fileType 文件类型	 * @param storePathIndex 文件路径byte	 * @return Result<String>	 * @throws IOException 	 */	public Result<String> upload(byte[] byteArray,String fileType,byte storePathIndex) throws IOException;	public Result<Boolean> delete(String group,String fileName) throws IOException;	public Result<Boolean> setMeta(String group,String fileName,Map<String,String> meta) throws IOException;	public Result<Map<String,String>> getMeta(String group,String fileName) throws IOException;	public void close() throws IOException;    /**     *      * @param file 文件     * @param fileid 主文件id,带group,如 g1/M00/00/00/aaaabbbbccc.jpg     * @param slavePrefix slave的后缀名 如200x200,最终的文件名将为g1/M00/00/00/aaaabbbbccc_200x200.jpg     * @param ext 扩展文件名，可以为null,如果为null，则从fileid里取     * @param meta 文件元数据，可以为null     * @return fileid 带group的文件fileid     * @throws IOException     */		/**	 * 	 * 功能说明：指定主文件id,存为slave	 * @author liuli	 * @creationDate 2015年5月22日 下午2:42:07	 * @param file 需要上传的文件	 * @param fileid 主文件id,带group,如 g1/M00/00/00/aaaabbbbccc.jpg	 * @param slavePrefix slave的后缀名 如_200x200,最终的文件名将为g1/M00/00/00/aaaabbbbccc_200x200.jpg	 * @param fileType 文件类型	 * @param meta 文件注释信息	 * @return Result<String>	 * @throws IOException 	 */    public Result<String> uploadSlave(File file, String fileid,String slavePrefix,String fileType,Map<String,String> meta) throws IOException;    /**     *      * 功能说明：判断连接是否关闭     * @author liuli     * @creationDate 2015年5月22日 下午2:43:15     * @return boolean     */    public boolean isClosed();}