package sivl.platform.mongodb.service;import java.util.List;import org.springframework.data.mongodb.core.query.Query;import sivl.platform.mongodb.model.BuzLogModel;import sivl.platform.mongodb.model.NetLogModel;/** * mongodb服务 * 目的： * @author lixuefeng * @createTime 2015年12月9日 * @version 1.0 */public interface MongodbService {		/**	 * 保存网络日志	 * @param buzLogModel	 */	void saveNetLog(NetLogModel nuzLogModel);		/**	 * 保存业务日志	 * @param buzLogModel	 * @return	 */	void saveBuzLog(BuzLogModel buzLogModel);		/**	 * 保存数据	 * <br/>目的：	 * @author lixuefeng	 * @date 2016年1月27日	 * @param obj	 */	void saveObject(Object obj);		/**	 * 根据查询条件查询对象集合<br/>	 * @see http://docs.spring.io/spring-data/data-mongo/docs/1.8.2.RELEASE/reference/html/#mongo.query	 * @author lixuefeng	 * @createTime 2015年12月23日	 * @param query	 * @param classes	 * @return	 */	<T>List<T> getListObject(Query query,Class<T> classes);		}