package sivl.safety.tasks.controller;import javax.annotation.Resource;import sivl.platform.common.constant.Constant;import sivl.safety.tasks.model.SchedulerModel;import sivl.safety.tasks.service.SchedulerService;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.util.StringUtils;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;/** * 任务控制中心 * @author lixuefeng * @createTime 2015年11月26日 * @version 1.0 */@Controller@RequestMapping("/tasks")public class TaskController {	@Resource	SchedulerService schedulerService;		@RequestMapping(value="/addTask",method = {RequestMethod.GET,RequestMethod.POST})	public String addTask(Model model,SchedulerModel schedulerBean){		if(StringUtils.isEmpty(schedulerBean.getCronExpression())){			model.addAttribute("msg", "日历参数为空!服务器拒绝！");			return Constant.ERROR_500.getValue();		}		schedulerService.schedule(schedulerBean);		return "";	}}